import random
import time
from tqdm import tqdm
from web3 import Web3
from web3.eth import AsyncEth
from loguru import logger
import asyncio

async def check_status_tx(privatkey, tx_hash):
    async_w3 = Web3(Web3.AsyncHTTPProvider('https://polygon-rpc.com'), modules={'eth': (AsyncEth,)}, middlewares=[])
    account = async_w3.eth.account.from_key(privatkey)
    address = account.address

    logger.info(f'{address} - жду подтверждения транзакции')

    while True:
        try:
            status = await async_w3.eth.get_transaction_receipt(tx_hash)
            status = status['status']
            if status in [0, 1]:
                return status
            time.sleep(1)
        except Exception as error:
            time.sleep(1)

async def sleep_indicator(secs):
    for i in tqdm(range(secs), desc='жду', bar_format="{desc}: {n_fmt}c /{total_fmt}c {bar}", colour='green'):
        await asyncio.sleep(1)

